<?phpclass ModelCatalogSeoKeywordGenerator extends Model {	public function getKeyword($keyword_id) {		$query = $this->db->query("SELECT keyword FROM " . DB_PREFIX . "url_alias WHERE query = '" .$keyword_id . "'");		if(isset($query->row['keyword'])){			return $query->row['keyword'];		}else{			return '';		}	}		public function SaveSEOKeyword($data) {		foreach($data as $key=>$val){			if($val!=""){				$query = $this->db->query("SELECT keyword FROM " . DB_PREFIX . "url_alias WHERE query = '" .$key . "'");				if(isset($query->row['keyword'])){					$this->db->query("UPDATE " . DB_PREFIX . "url_alias SET keyword = '".$val."' WHERE query = '" .$key . "'");				}else{					$this->db->query("INSERT INTO  " . DB_PREFIX . "url_alias (`query`, `keyword`) VALUES ('" .$key . "', '".$val."')");				}			}		}	}		public function getAllCategorys($data=array()) {		$category_description_data = array();		$query = $this->db->query("SELECT * FROM " . DB_PREFIX . "category_description ");		return $query->rows;	}	}?>